import React, {
    Component
} from 'react';
import {
    withStyles
} from '@material-ui/core/styles';
import Avatar from '@material-ui/core/Avatar';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Slide from '@material-ui/core/Slide';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';

const styles = theme => ({
    card: {
        width: 400,
        margin: 20,
        background: "#32333a",
    },
    media: {
        height: 200,
    },
    tilte: {
        color: "#ffffff"
    },

    summary: {
        color: "#ffffff"
    },

    bigAvatar: {
    },
    newsDialog: {
        background: "#32333a",
        color: "#ffffff"
    },
});


function Transition(props) {
  return <Slide direction="up" {...props} />;
}


class NewsListitem extends Component {

    constructor(props){
        super(props)
        this.state = {
            open: false,
            maxWidth: 'md'
        }
        this.handleDialog = this.handleDialog.bind(this)
        this.handleClose = this.handleClose.bind(this)
    }

    handleDialog() {
        this.setState(prevState => ({
            open: !prevState.open
        }))
    }

    handleClose() {
        this.setState(prevState => ({
            open: false
        }))
    }

    render() {

        const { classes } = this.props;
        const { content, cover, title, summary, author_avatar} = this.props.new

        return (
        <Slide in={true}
            >
            <div>
                <Card onClick={this.handleDialog} className={classes.card} justify="center">
                    <CardActionArea>
                        {/* <Avatar alt="Remy Sharp" src={author_avatar} className={classes.bigAvatar} /> */}
                        <CardMedia
                        className={classes.media}
                        image={cover}
                        title={title}
                        >
                        </CardMedia>
                        <CardContent>
                            <ListItem>
                                <Avatar alt="Remy Sharp" src={author_avatar} className={classes.bigAvatar}>
                                </Avatar>
                                <ListItemText>
                                    
                                    <Typography primary className={classes.tilte} component="h1">
                                        {title}
                                    </Typography>
                                
                                </ListItemText>
                            </ListItem>
                        </CardContent>
                    </CardActionArea>
                </Card>
                <Dialog
                    TransitionComponent={Transition}
                    open={this.state.open}
                    scroll='body'
                    onClose={this.handleClose}
                    maxWidth={this.state.maxWidth}
                    aria-labelledby="scroll-dialog-title"
                    >
                    <DialogTitle 
                        className={classes.newsDialog}
                        id="scroll-dialog-title">
                        <h2 className={classes.tilte} component="h1">
                            {title}
                        </h2>
                    </DialogTitle>
                    <DialogContent
                        className={classes.newsDialog}>
                            <Typography className={classes.tilte} component="p" dangerouslySetInnerHTML={{ __html: content }} >
                                {/* 渲染html  dangerouslySetInnerHTML*/}
                            </Typography>
                    </DialogContent>
                </Dialog>
            </div>
        </Slide>   

        )
    }

}

export default withStyles(styles)(NewsListitem)